function add_product_to_cart(el){
    let prod = JSON.parse(localStorage.getItem('cart'));
    let count = 0;
    prod.forEach(function(){
        count++;
    })
    let cart_body = document.querySelector('.cart-body');
    
    let prod_count_div = document.createElement('div');
    let count_p = document.createElement('p');
    count_p.innerHTML = `MY BAG  (${count})`;
    count_p.setAttribute('class','cart-count');
    prod_count_div.append(count_p);
    cart_body.append(prod_count_div);

}

let id = 'input';
function append_product(el){
    let cart_body = document.querySelector('.cart-body');
    let product_div = document.createElement('div');
    product_div.setAttribute('class','product-div');


    let prod_image_div = document.createElement('div');
    prod_image_div.setAttribute('class', 'prod-image');
    let prod_image_url = document.createElement('img');
    prod_image_url.src = el.Image;
    prod_image_div.append(prod_image_url);
    product_div.append(prod_image_div);
    cart_body.append(product_div)

    let prod_name_div = document.createElement('div');
    prod_name_div.setAttribute('class','prod-name-div')
    let prod_name = document.createElement('p');
    prod_name.innerHTML = el.Name;
    prod_name_div.append(prod_name);
    product_div.append(prod_name_div)
    cart_body.append(product_div)


    let prod_price_div = document.createElement('div');
    prod_price_div.setAttribute('class','prod-price-div')
    let prod_price = document.createElement('p');
    prod_price.innerHTML = `₹ ${el.Price}`
    prod_price_div.append(prod_price);
    product_div.append(prod_price_div)
    cart_body.append(product_div);

    let quant_div = document.createElement('div');
    quant_div.setAttribute('id', 'quant-div')



    let quant_box = document.createElement('input');
    quant_box.setAttribute('id',`${id}`)
    quant_box.readOnly = 'readonly';
    quant_box.value = '1';

    //quantity increase decrease button start


    let button_low = document.createElement('button');
    button_low.textContent = '-';
    button_low.setAttribute('id',`{id}`)
    button_low.addEventListener('click', function(){
        decrease(id);
    })

   
    
 
    let button_high = document.createElement('button');
    button_high.textContent = '+';
    button_high.addEventListener('click', function(){
        increase();
    })

    id+=1;
    //quantity increase decrease button ends

    quant_div.append(button_low,quant_box, button_high);
    product_div.append(quant_div);
    console.log(product_div)
    cart_body.append(product_div)

    let grand_total_div =document.createElement('div');
    grand_total_div.setAttribute('class','grand-total-div');
  
}
function append_lower_part(){
    let container = document.querySelector('.container')
    let cash_back_text_div = document.createElement('div')
    cash_back_text_div.setAttribute('class','cashback-text-div');

    let cash_back_text = document.createElement('p');
    cash_back_text.innerHTML = `You will earn  <b>₹ 102 MyGlammXO Points </b> as cashback on this order.`
   
    let grand_total = document.createElement('p');
    grand_total.innerHTML = grand_total;

    cash_back_text_div.append(cash_back_text, grand_total);


    container.append(cash_back_text_div)
}

function get_cart_total_amount(){
    let cart_prod = JSON.parse(localStorage.getItem('cart'));
    let grand_total = 0;
    cart_prod.forEach(function(el){
        grand_total +=el.Price;
    })
    
    let container = document.querySelector('.container')
    let cash_back_text_div = document.createElement('div')
    cash_back_text_div.setAttribute('class','cashback-text-div');

    let cash_back_text = document.createElement('p');
    cash_back_text.innerHTML = `You will earn  <b>₹ 102 MyGlammXO Points </b> as cashback on this order.`
   
    let grand_total_price = document.createElement('p');
    grand_total_price.innerHTML = `GRAND TOTAL  <b> ₹ ${grand_total} </b>`;

    localStorage.setItem("grand_total", JSON.stringify(grand_total));

    cash_back_text_div.append(cash_back_text, grand_total_price);

    let checkout_button_div = document.createElement('div');
    checkout_button_div.setAttribute('class', 'check-out-btn-div')
    let checkout_button = document.createElement('button');

    checkout_button.setAttribute('class','check-out-btn');
    checkout_button.textContent = 'PROCEED TO CHECKOUT';
    checkout_button.addEventListener('click', function(){
        location.href = 'address.html'
    })
    checkout_button_div.append(checkout_button);
    
    let hr_line = document.createElement('hr');
    hr_line.style = 'margin-top:30px;margin-bottom:20px';



    let footer_text_div = document.createElement('div');
    footer_text_div.setAttribute('class', 'foot-text-div')
    let footer_text = document.createElement('p');
    footer_text.innerText = '© MyGlamm';
    footer_text_div.append(footer_text)


    container.append(cash_back_text_div,checkout_button_div,hr_line,footer_text_div)


}



function get_cart_product(){
    let prod = JSON.parse(localStorage.getItem('cart'));
    if(prod == null){
        let cart_body = document.querySelector('.cart-body');
        let image_div = document.createElement('div');
        image_div.setAttribute('class', 'empty-cart-image');
        let image = document.createElement('img');
        image.src = 'data:image/png;base64,'
        image_div.append(image)
        cart_body.append(image_div)

    }
    else{
        add_product_to_cart();
        prod.forEach(function(el){
        append_product(el)
        }) 
        get_cart_total_amount();
    }
}
get_cart_product();

